                         loader: 'postcss-loader',
                         options: {
                             config: {
                                 path: './tools/postcss.config.js',
                             },
                         },
                     },
                 ],
             },

             // Rules for images
             {
                 test: reImage,
                 oneOf: [
                     // Inline lightweight images into CSS
                     {
                         issuer: reStyle,
                         oneOf: [
                             // Inline lightweight SVGs as UTF-8 encoded DataUrl string
                             {
                                 test: /\.svg$/,
                                 loader: 'svg-url-loader',
                                 options: {
                                     name: staticAssetName,
                                     limit: 4096, // 4kb
                                 },
                             },

