 // Configuration for the client-side bundle (client.js)
 // -----------------------------------------------------------------------------

 const clientConfig = {
     ...config,

     name: 'client',
     target: 'web',

     entry: {
         client: ['babel-polyfill', './src/client.js'],
     },

